import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { toast } from "react-toastify";
import FileUploadCrud from "../../../components/forms/FileUploadCrud";
import ProductCreateFormCrud from "../../../components/forms/ProductCreateFormCrud";
import AdminNav from '../../../components/nav/AdminNav'
import { getCategories } from "../../../functions/category";
import { createProduct } from "../../../functions/product";
const ImProductCreate = () => {
    const [values, setValues] = useState('');
    const [subOptions, setSubOptions] = useState([]);
    const [showSub, setShowSub] = useState(false);
    const [loading , setLoading] = useState(false);
    const {user} = useSelector((state) => ({...state}));
    useEffect(()=>{
        loadCategories();
    },[]);
    const loadCategories = () => 
        getCategories().then((c) =>  setValues({...values, categories: caches.data }));

    const handleSubmit = (e) => {
        e.preventDefault();
        createProduct(values,user.token)
        .then((res) => {
            console.log(res);
            window.alert(`"${res.data.title}" is created`);
            window.location.reload();
        })
        .catch((err) => {
            console.log(err);
            toast.error(err.response.data.err);
        });
    };

    const handleChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.values});
    };

    const handleCatagoryChange = (e) => {
        e.preventDefault();
        console.log("Clicked category",e.target.values);
        setValues({...values})
    };  

    return(
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <AdminNav />
                </div>

                <div className="col-md-10">
                    <div className="p-3">
                        <FileUploadCrud 
                            values={values}
                            setValues={setValues}
                            setLoading={setLoading}
                        />
                    </div>
                    <ProductCreateFormCrud 
                        handleSubmit={handleSubmit}
                        handleChange={handleChange}
                        setValues={setValues}
                        values={values}
                        handleCatagoryChange = {handleCatagoryChange}
                        subOptions={subOptions}
                        showSub={showSub}
                    />
                </div>
            </div>
        </div>
    );
}

export default ImProductCreate;
